create table person (
    personid int primary key auto_increment,
    firstname varchar(25) not null,
    lastname varchar(40) not null,
    email varchar(60) not null unique,
    contact varchar(20) not null,
    gender enum("Male","Female") not null,
    dob date not null,
    nationality varchar(50) not null,
    image varchar(100)
);

create table tourguide(
    personid int,
    tourguideid smallint unique,
    password varchar(50),
    qualification varchar(20),
    biography mediumtext,
    location varchar(50),
    dateofemployment date,
    foreign key (personid) references person(personid)
);

create table traveller(
	personid int,
	password varchar(50),
	foreign key (personid) references person(personid)
);

create table eventcreater(
	personid int,
	password varchar(50),
	foreign key (personid) references person(personid)
);

create table place(
	placeid int primary key auto_increment,
	name varchar(100) not null,
	location varchar(100) not null,
	contact varchar(15),
	description mediumtext,
	imageone varchar(100) not null,
	imagetwo varchar(100) ,
	imagethree varchar(100) 
);

create table hotel(
	placeid int,
	manager varchar(50),
	foreign key (placeid) references place(placeid)
);

create table restaurant(
	placeid int,
	opentime time,
	closetime time,
	manager varchar(50),
	foreign key (placeid) references place(placeid)
);

create table terminal(
	placeid int,
	foreign key (placeid) references place(placeid)
);

create table touristsite(
	placeid int,
	feeghanaadult float,
	feeghanachild float,
	feeforeignadult float,
	feeforeignchild float,
	foreign key (placeid) references place(placeid)
);

create table event(
	 eventid int primary key auto_increment,
	eventname varchar(50) not null,
	description mediumtext,
	startdate date not null,
	starttime time not null,
	enddate date,
	endtime time,
	rate float not null,
	flyer varchar(100),
	CONSTRAINT checkdate CHECK (startdate <= enddate)
);

create table tourevent(
	eventid int,
	foreign key (eventid) references event(eventid)
);

create table category(
	categoryid tinyint primary key auto_increment,
	name varchar(20)
);

create table otherevent(
	eventid int,
	createdby int,
	category tinyint,
	foreign key (eventid) references event(eventid),
	foreign key (createdby) references person(personid),
	foreign key (category) references category(categoryid)
);

create table tourlocation(
	eventid int,
	placeid int,
	foreign key (eventid) references event(eventid),
	foreign key (placeid) references place(placeid)
);

create table attendance(
	eventid int,
	personid int,
	date date,
	foreign key (eventid) references event(eventid),
	foreign key (personid) references person(personid)
);

create table eventcreation(
	eventid int,
	personid int,
	date date,
	foreign key (eventid) references event(eventid),
	foreign key (personid) references person(personid)
);

create table eventguide(
	eventid int,
	personid int,
	foreign key (eventid) references event(eventid),
	foreign key (personid) references person(personid)
);

delete from touristsite;
delete from place;

